            if(isPic(_pic)&&!/@Referer=/.test(_pic)){
                _pic+='@Referer='
            }
            let it={
                title:pdfh(list[i],t[1]),
                pic_url:_pic,
                desc:pdfh(list[i],t[3]),
                url:ej?pd(list[i],t[4]):pd(list[i],t[4])+lazy
            };
            if(t.length>5){//内容，用于搜索
