});"},{"col_type":"movie_3","name":"maoss","path":"mao","rule":"function maoss(jxurl, ref, key) {
    try {  
        function tools(text, key, iv) {    
        eval(getCryptoJS());
        var key = CryptoJS.enc.Utf8.parse(key);
        var iv = CryptoJS.enc.Utf8.parse(iv);
        return CryptoJS.AES.decrypt(text, key, {
            iv: iv,
            padding: CryptoJS.pad.Pkcs7
        }).toString(CryptoJS.enc.Utf8);
    };

        let headers = {headers: {"Referer": ref || ""}};
        var html = ref ? request(jxurl, headers) : request(jxurl,{timeout: 5000});   
       if (/&btwaf=/.test(html)) {
            html = request(jxurl + "&btwaf" + html.match(/&btwaf(.*?)"/)[1], {headers: {"Referer": ref}});
        }  
        var iv = html.split('_token = "')[1].split('"')[0];  
        var text=html.split('url":')[1].split('"')[1].split('"')[0]
        return tools(text,key,iv);
