    }
});
//log(html);
let data = JSON.parse(html);
try {
    vod_id = data.data.vodId;
} catch (e) {
    //log(MY_URL);
    vod_id = MY_URL.match(/vodId=(\d+)/)[1];
}
//var vod_id = MY_URL.match(/vodId=(\d+)/)[1];

let url = 'http://video-static.czjsbo.com/static/video/detail/' + vod_id + '.json';
let detail = JSON.parse(request(url)).data;
//log(detail);
var d = [];
d.push({
    title: detail.vodName + '\n' + detail.vodUpdateTip + '\n' + detail.typeName,
    desc: detail.vodPubdate + '\n' + detail.vodYear + '\n' + detail.vodArea,
    url: host + detail.vodPic,
    pic_url: host + detail.vodPic,
    col_type: 'movie_1_vertical_pic_blur',
});

try {
    var info = detail.vodBlurb;
} catch (e) {
    var info = '暂无简介'
}
d.push({
    title: color("剧情简介", "#098AC1"),
    desc: small(info),
    col_type: "text_1",
    extra: {
        lineVisible: false
    },
    url: $('hiker://empty#noHistory##noRecordHistory#').rule((info) => {
        let d = [];
        d.push({
            title: info,
            col_type: "long_text"
        });
        setResult(d);
    }, info)
});
let vodPlayUrl = detail.vodPlayUrl;
let vodPlayNote = detail.vodPlayNote;

//log(vodPlayUrl);

if (vodPlayUrl.length >= 16) {
    let s1 = vodPlayUrl.substring(0, 10);
    let s2 = vodPlayUrl.substring(16);
    vodPlayUrl = s1 + s2;
}
let key = "";
vodPlayUrl = rsaDecrypt(
    vodPlayUrl, key, {
        type: 2,
        "long": 2
    }
);

//log(vodPlayUrl);
var tabs = JSON.parse(vodPlayUrl);
var urls = tabs[0].list;
let showOrder = getMyVar('顺序', '正续') === '逆序' ? color('(☆逆序↑)', '#228be6') : color('(★正序↓)', '#d0aa344');
d.push({
    title: small(color('选集列表共计', '#098AC1') + color(tabs.length, '#d96715') + color('条线路', '#098AC1')) + blank() + right(small(showOrder)),
    col_type: 'text_1',
    url: $('#noLoading#').lazyRule(() => {
        if (getMyVar('顺序', '正续') === '逆序') {
            putMyVar('顺序', '正续');
        } else {
            putMyVar('顺序', '逆序');
        }
        refreshPage(true);
        return 'toast://已切换顺序为:' + getMyVar('顺序', '正续')
    }),
    extra: {
        lineVisible: false
    }
});

d.push({
    title: detail.vodDownServerGroup,
    url: 'hiker://empty',
    col_type: "scroll_button",
});
if (getMyVar('顺序', '正续') === '逆序') {
    urls = urls.reverse();
}
for (let u of urls) {
    d.push({
        // title:html,
        //title: vodPlayUrl,
        title: u.name,
        //col_type: 'rich_text',
        //col_type:'text_5'
        url: $().lazyRule((u) => {

            function gg(str) {
                if (str.startsWith("http://")) {
                    return str.substring(0, str.substring(7).indexOf("/") + 7);
                }
                return str.startsWith("https://") ? str.substring(0, str.substring(8).indexOf("/") + 8) : "";
            }

            function gg2(str) {
                let a1 = gg(str);
                //log(str);
                //log(a1);
                let substring = str.substring(a1.length);
                //j.d(substring, "(this as java.lang.String).substring(startIndex)");
                let valueOf = "" + new Date().getTime();
                let key = md5(substring + "TBW1zF4p1j" + valueOf);
                return str + "?key=" + key + "&time=" + valueOf;
            }
            let url = cacheM3u8(gg2(u));
            let f = url.split("##")[0];
            let c = fetch(f);
            let cs = c.split("\n");
            for (let i in cs) {
                if (cs[i].startsWith("#EXT-X-KEY:")) {
                    //log(cs[i])
                    let kurl = cs[i].match(/URI="(.*?)"/)[1];
                    let keurl = gg2(kurl);
                    //log(request(keurl))                    
                    cs[i] = cs[i].replace(/".*?",/, '"' + keurl + '",');
                    //log(cs[i])

                }
                if (cs[i].startsWith("http")) {
                    cs[i] = gg2(cs[i]);
                }
            }
            //log(cs)            
            writeFile(f, cs.join("\n"));
            let header = ';{Referer@video3.douquwuxian.com&&User-Agent@okhttp/p2p-1.2/normal}';
            return f + "##" + url.split("##")[1] + header;           
        }, u.url),
    });
}
setResult(d);

