var lazy = $('').lazyRule(() => {
    var playUrl = "";
    try {
        var html = request(input, {});
        var player_data = JSON.parse(html.match(/r player_.*?=(.*?)</)[1]);

        var fro = player_data.from;
        playUrl = player_data.url;

        if (player_data.encrypt == '1') {
            playUrl = unescape(player_data.url);
        } else if (player_data.encrypt == '2') {
            playUrl = unescape(base64Decode(player_data.url));
        };

        //如果播放地址存在m3u8等扩展名直接返回
        if (playUrl.match(/\.m3u8|\.mp4|obj\/tos|\.flv/) && !playUrl.match(/html|m3u8\.tv|\&next|ac\=dm|\=http/)) {
            if (playUrl.indexOf('bilivideo') != -1) {
                return playUrl + ';{Referer@https://www.bilibili.com&&User-Agent@Mozilla/5.0}';
            } else if (playUrl.indexOf('titan.mgtv.com') != -1) {
                return playUrl + '#isVideo=true#' + ';{Referer@www.mgtv.com&&User-Agent@Mozilla/5.0}';
            } else if (playUrl.indexOf('com/gzc') != -1) {
                return playUrl + ';{Referer@https://www.aifengys.cc/}';
            } else {
                return playUrl;
            }
        }

        showLoading("正在进行检索，请稍候...");
        var video = 'x5Rule://' + input + '@' + $.toString(() => {
            //fba.log(fba.getUrls());
            var urls = _getUrls();
            if (urls.length > 0) {
