                    if (urls[i].match(/\.m3u8|\.mp4|obj\/tos|\.flv/) && !urls[i].match(/html|m3u8\.tv|\&next|ac\=dm|\=http/)) {
                        //fy_bridge_app.log(urls[i])
                        if (urls[i].indexOf('bilivideo') != -1) {
