function 常规嗅探(input) {
		showLoading("资源嗅探中，请稍候……");
		var video = 'x5Rule://' + input + '@' + $.toString((input) => {
			var urls = _getUrls();
			if (window.c == null) window.c = 0;
			window.c++;
			if (window.c * 250 > 5 * 1000) {
				//fba.log("嗅探超时");
				fba.hideLoading();
				return fba.parseLazyRule($$$('').lazyRule((input) => {
					showLoading("嗅探超时，启用解析");
					try {
						eval(fetch(JSON.parse(fetch('hiker://files/cache/MyParseSet.json')).cj));
						let ts = /ixigua|iqiyi|qq.com|mgtv|le.com|bili|sohu|youku|pptv|cctv|1905.com/;
						if(!input.match(ts)){
							let From = JSON.parse(request(input).match(/var player_.*?=(.*?)</)[1]);
							let 加密 = From.encrypt;
							let 来自 = From.from;
							原链 = From.url;
							//log(From)
							if (加密 == '1') {
								原链 = unescape(原链);
							} else if (加密 == '2') {
								原链 = unescape(base64Decode(原链));
							}
							hideLoading();
							if (/\.mp4|\.m3u8|\.flv/.test(原链)) {
								return 原链;
							} else if (ts.test(原链)) {
								return aytmParse(原链);//爱优腾等地址
							} else {
								return playParse.playerS(input);//盗版站地址
							}
						} else {
							return aytmParse(input)
						}
					} catch (e) {
						showLoading('解析出错，通免嗅探中');
						requireCache('https://code.aliyun.com/lzk23559/PublicRule/raw/master/x5rule.js', 24);
						return x5rule(input, input);
					}
				}, input))
			} else {
				for (var i in urls) {
					if (urls[i].match(/\.mp3|\.mp4|\.m3u8/)) {
						//fba.log(urls[i]);
						return urls[i].replace(/.*url=/, '')
					}
				}
			}
		}, input);
