                    url:下载标题.includes("码") ? 下载链接+"?pwd="+下载标题.split("码")[1].replace(/:|：| /g,"")+lazy:下载链接.replace('https://www.aliyundrive.com','hiker://page/detail?rule=云盘汇影&url='+'https://www.aliyundrive.com').replace("?pwd=null",""),
                    col_type: list.length > 4 ? 'text_5' : 'text_2'
                });
}

            }
        } catch (e) {
            d.push({
                title: '没有选集哟，不信点图片去看看👀',
                col_type: 'text_center_1'
            });
        }

    }
}


