js:
var d = [];
var getRangeColors = function(){
        return '#'+('00000'+(Math.random()*0x1000000<<0).toString(16)).substr(-6);
      }
const 分类颜色 = getRangeColors()
const 大类定位 = '.stui-header__menu'
const 拼接分类 = 'body&&.stui-screen__list'
const 小类定位 = 'body&&a:not(:matches(首页|明星|资讯|敏感|专题))'
const 分类标题 = 'a&&Text'
const 分类链接 = 'a&&href.js:input.replace(/type/, "vodshow/id")'
MY_URL = MY_URL.replace('hiker://empty##', '')

let true_url = getVar(MY_RULE.url, MY_URL)
const page = MY_URL.match(/page\/(.*)\./)[1]
true_url = true_url.replace('.html', '/page/' + page + '.html')

var html = fetch(true_url, {
    headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0)'
    }
})

eval(JSON.parse(request('hiker://page/dt')).rule);

const list = parseDomForArray(html, '.stui-vodlist&&li')
list.forEach(video => {
    d.push({
        title: pdfh(video, 'a&&title'),
        desc: pdfh(video, '.pic-text&&Text'),
        img: pd(video, 'a&&data-original') + "@Referer=",
        url: pd(video, 'a&&href') + '#immersiveTheme#',
        col_type: 'movie_3',
    })
})
setResult(d);

