detail_find_rule:
js:
var d = [];

var html = getResCode();
eval(JSON.parse(request('hiker://page/lazy')).rule);

//线路名列表的定位
var arts = parseDomForArray(html, 'body&&h3:gt(0)');

//线路名列表名称的定位
var tabs = [];
for (var i in arts) {
    tabs.push(parseDomForHtml(arts[i], 'title&&Text'))
}

//播放列表的列表的定位
var conts = parseDomForArray(html, 'body&&.stui-content__playlist');
var lists = [];
for (var i in conts) {
    lists.push(parseDomForArray(conts[i], 'ul&&li'))
}

var 分类 = parseDomForHtml(html, '.data&&Text');

var 主演 = parseDomForHtml(html, '.data,1&&Text');

var 导演 = parseDomForHtml(html, '.data,2&&Text');

var 更新 = parseDomForHtml(html, '.data,3&&Text');

var 图片 = parseDom(html, '.lazyload&&data-original');

var 简介 = parseDomForHtml(html, '.desc&&Text').replace('简介：','').replace('详情','');

var Color = "#f13b66a";
var Color1 = "#098AC1";
function getHead(title) {
    return '‘‘’’<strong><font color="' + Color + '">' + title + '</front></strong>';
}
let line_model = 8;
let line_type_model = 'blank_block';
d.push({
    title: 分类 + '\n' + 主演,
    desc: 导演 + '\n' + 更新,
    pic_url: 图片,
    url:MY_URL,
    col_type: 'movie_1_vertical_pic_blur',
    extra:{gradient: true}
});
d.push({
    title: '‘‘’’<small><font color="#ff148e8e">简介：' + 简介.substr(0, 55) + '...</font><small><font color="red">详情</font></small></small>',
    url: 'hiker://empty#' + '\n' + 简介 + `@rule=js:var res = {}; var d = [];d.push({title:'影片简介：'+ MY_URL.split('hiker://empty#')[1],col_type: 'long_text'});res.data = d; setHomeResult(res);`,
    col_type: 'text_1'
});

for (let i = 0; i < line_model; i++) {
    d.push({
        col_type: line_type_model
    })
}
function setTabs(tabs, vari) {
    for (var i in tabs) {
        var url = "#noLoading#@lazyRule=.js:putVar('" + vari + "', '" + i + "');refreshPage(false);'toast://切换成功！';'#noHistory#hiker://empty'";
        d.push({
            title: getVar(vari, '0') == i ? getHead(tabs[i]) : tabs[i],
            url: url,
            col_type: 'scroll_button'
        })
    }

}
function setLists(lists, index) {
    d.push({
        col_type: 'line_blank'
    })
}
if (getVar('shsort') == '1') {
    var sx = '‘‘’’<font color="#6639a6">排序</font></small>' + '<small><font color="#f6416c">↑</font></small>' + '<small><font color="#2b2e4a">↓</font></small>';
} else {
    var sx = '‘‘’’<font color="#0aa344">排序</font></small>' + '<small><font color="#2b2e4a">↑</font></small>' + '<small><font color="#f6416c">↓</font></small>';
}

var Color = "#3490de";
var Color1 = "#ea5455";

function setLists(lists, index) {
    d.push({
        title: '‘‘’’<span style="color:' + Color1 + '"><small>选集列表</small>' + '\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t<span style="float:right">' + '<small>' + sx + '</small>',
        url: `#noLoading#@lazyRule=.js:let conf = getVar('shsort');if(conf=='1'){putVar({key:'shsort', value:'0'});}else{putVar({key:'shsort', value:'1'})};refreshPage();'toast://切换排序成功';'#noHistory#hiker://empty'`,
        col_type: 'text_center_1'
    })
    var list = lists[index];

    if (getVar('shsort') == '1') {
        try {
            for (var j = list.length - 1; j >= 0; j--) {
                d.push({
                    title: parseDomForHtml(list[j], 'a&&Text').replace(/第|集|话|期/g, ''),
                    url:parseDom(list[j], 'a&&href')+lazy,
                    col_type: list.length > 3 ? 'text_4' : 'text_3'
                });
            }
        } catch (e) {
            d.push({
                title: '没有选集哟，不信点图片去看看👀',
                col_type: 'text_center_1'
            });
        }
    } else {
        try {
            for (var j = 0; j < list.length; j++) {
                d.push({
                    title: parseDomForHtml(list[j], 'a&&Text').replace(/第|集|话|期/g, ''),
                    url:parseDom(list[j], 'a&&href') +lazy,extra: {
                    blockRules: ['.css', '.gif', '.jpg', '.jpeg', '.png', '.ico', '.svg', '.m3u8', '.mp4']
                },
                    col_type: list.length > 3 ? 'text_4' : 'text_3'
                });
            }
        } catch (e) {
            d.push({
                title: '没有选集哟，不信点图片去看看👀',
                col_type: 'text_center_1'
            });
        }

    }
}
setTabs(tabs, MY_URL);
setLists(lists, getVar(MY_URL, '0'));
d.push({
    title: '<br>',
    col_type: 'rich_text'
});
setResult(d);

