                        putVar(MY_RULE.url, input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        index: index,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        }
    })
}
