    var play = CryptoJS.AES.decrypt(html.match(/getVideoInfo\("(.*?)"/)[1], CryptoJS.enc.Utf8.parse('C04AD764B8CD4CE9'), {
        'iv': CryptoJS.enc.Utf8.parse(bt_token)
    }).toString(CryptoJS.enc.Utf8)
    if (/mgtv/.test(play)) {
        play += ';{User-Agent@Mozilla/5.0 (Windows NT 10.0)}';
    } else {
        //   play += ';{User-Agent@Mozilla/5.0 (Windows NT 10.0)&&Referer@https://fantuan.tv/}'
        play = cacheM3u8(play, {
